package carsharing.model.dao;import carsharing.db.DbClient;import carsharing.model.entity.Company;import java.util.List;public class DbCompanyDao implements CarShareDAO<Company> {    private static final String CREATE_COMPANY_TABLE =            "CREATE TABLE IF NOT EXISTS COMPANY " +            "(ID INT PRIMARY KEY AUTO_INCREMENT, " +            "NAME VARCHAR(255) UNIQUE NOT NULL)";    private static final String INSERT_COMPANY = "INSERT INTO COMPANY (NAME) " +                                                 "VALUES " +                                                 "('%s')";    private static final String SELECT_ALL = "SELECT * FROM COMPANY";    private static final String SELECT_BY_ID = "SELECT * FROM COMPANY WHERE ID = ?";    private final DbClient dbClient;    public DbCompanyDao(DbClient dbClient) {        this.dbClient = dbClient;        dbClient.run(CREATE_COMPANY_TABLE);    }    @Override    public List<Company> findAll() {        return dbClient.selectForList(SELECT_ALL);    }    @Override    public Company findById(int id) {        return dbClient.select(SELECT_BY_ID.replace("?", String.valueOf(id)));    }    @Override    public void add(Company company) {        dbClient.run(String.format(INSERT_COMPANY, company.getName()));    }}