package carsharing.model.menu;import carsharing.model.enums.MenuNames;import java.util.LinkedHashMap;import java.util.Map;public class Menu {    private static final Map<MenuNames, Map<Integer, String>> userMenu =            new LinkedHashMap<>();    public Menu() {        createMenu();    }    public static void printMenu(MenuNames menuName) {        userMenu.get(MenuNames.valueOf(menuName.toString()))                .forEach((k, v) -> System.out.println(                        k + ". " + v + (k == 0 ? "\n" : "")));    }    private void createMenu() {        Map<Integer, String> step1 = new LinkedHashMap<>();        step1.put(1, "Log in as a manager");        step1.put(2, "Log in as a customer");        step1.put(3, "Create a customer");        step1.put(0, "Exit");        userMenu.put(MenuNames.MAIN_MENU, step1);        Map<Integer, String> step2 = new LinkedHashMap<>();        step2.put(1, "Company list");        step2.put(2, "Create a company");        step2.put(0, "Back");        userMenu.put(MenuNames.MANAGER_MENU, step2);        Map<Integer, String> step3 = new LinkedHashMap<>();        step3.put(1, "Car list");        step3.put(2, "Create a car");        step3.put(0, "Back");        userMenu.put(MenuNames.COMPANY_MENU, step3);        Map<Integer, String> step4 = new LinkedHashMap<>();        step4.put(1, "Rent a car");        step4.put(2, "Return a rented car");        step4.put(3, "My rented car");        step4.put(0, "Back");        userMenu.put(MenuNames.CUSTOMER_MENU, step4);        Map<Integer, String> step5 = new LinkedHashMap<>();        step5.put(1, "Choose a company");        step5.put(0, "Back");        userMenu.put(MenuNames.SELECT_COMPANY_FOR_RENT, step5);        Map<Integer, String> step6 = new LinkedHashMap<>();        step6.put(1, "Choose a car");        step6.put(0, "Back");        userMenu.put(MenuNames.SELECT_CAR_FOR_RENT, step6);    }    public String getMenuOption(MenuNames menuName, int option) {        return userMenu.get(MenuNames.valueOf(menuName.toString())).get(option);    }}